    private Work makeWork(String bibtex){
        Work work = new Work();
        Citation citation = new Citation();
        citation.setWorkCitationType(CitationType.BIBTEX);
        citation.setCitation(bibtex);
        work.setWorkCitation(citation);
        return work;
    }
