	            html : $('<div style="font-size: 50px; line-height: 60px; padding: 20px; text-align:center">' + om.get('common.processing') + '&nbsp;<i id="ajax-loader" class="glyphicon glyphicon-refresh spin green"></i></div>'),
	            width: '400px',
	            height:"100px",
	            close: '',
	            escKey:false,
	            overlayClose:false,
